import {
	GET_PRODUCTS,
	GET_PRODUCT,
	GET_PRODUCT_REVIEWS,
} from '../actions/types';


export default function(state={products:[], product:{}, reviews:[]}, action){
	switch(action.type){


	case GET_PRODUCTS:
		return {...state, products:action.payload};


	case GET_PRODUCT:
		return {...state, product:action.payload};




	default:
		return state;
	}
}
