import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import { 
	getDepartments,
} from '../actions/index'


class HeaderSub extends Component {
	constructor(){
		super()

		this.state = {

		}
	}


	render(){

		const departmentLabels = this.props.departments.map(department => {
			return (
				<span style={{flex:'1'}} className='topbar white' ><strong>{department.name}</strong></span>
			)
		});


		return(
			<div className='background-black' style={{height:'72px', paddingLeft:'30px', paddingRight:'30px', display:'flex', alignItems:'center'}}>
				<div style={{width:'15%', display:'inline-block'}}>
					<span className='topbar pink'>SHOPMATE</span>
				</div>

				<div style={{display:'flex', width:'40%', justifyContent:'space-around', textAlign:'center'}}>
					{departmentLabels}
				</div>

				<div style={{width:'25%', display:'inline-block', textAlign:'center'}}>
					<input placeholder='Search' className='form-control'/>
				</div>

				<div style={{width:'15%', display:'inline-block', textAlign:'center'}}>
					<span className='white'>User</span>
				</div>
			</div>

		);
	}
}



function mapStateToProps(state){
	return { 
		departments:state.departments.departments
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({

	}, dispatch);
};


HeaderSub = connect(mapStateToProps, mapDispatchToProps)(HeaderSub);

export default withRouter(HeaderSub);
