import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import Header from './components/Header'
import HeaderNavigation from './components/HeaderNavigation'
import ProductCards from './components/ProductCards'

import { 
	searchProducts
} from './actions/index';

import querystring from 'query-string'


class Browse extends Component {
	constructor(){
		super()

		this.state = {

		}
	}


	componentDidMount(){

		window.scrollTo(0, 0)

		//Load department
		let query = 'Thomas'

		console.log('location', this.props.location.search)

		this.props.searchProducts(query)


	}





	render(){

		let categoryLabels;

		if(this.props.categories.length > 0){
			categoryLabels = this.props.categories.map(category => {
				return (
					<div style={{paddingLeft:'15px'}} key={category.category_id}>
						<h3 className='link'>{category.name}</h3>
					</div>
				)
			});
		}

		return(
		    <div>
		    	Browse
		     	<div className='background-light-gray'>
			     	<div style={{paddingTop:'15px'}} className='container-fluid'>
			     	<div className='row'>

				     	<div className='col-3'>
					     	<div className='bottom-shadow' style={{backgroundColor:'white'}}>
					     		<div style={{padding:'15px'}}>
					     			<h3 className='light-gray'>Categories</h3>
					     			{categoryLabels}
					     		</div>
					     	</div>
				     	</div>

				     	<div className='col-9'>
				     		<ProductCards/>
				     	</div>

			     	</div>
			     	</div>
		     	</div>

		    </div>
		)
	}
}



function mapStateToProps(state){
	return { 	
		categories:state.categories.categories
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		searchProducts,
	}, dispatch);
};

Browse = connect(mapStateToProps, mapDispatchToProps)(Browse);
export default withRouter(Browse);
