import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';


import shirt1 from '../images/shirt1.png'
import shirt2 from '../images/shirt2.png'
import shirt3 from '../images/shirt3.png'



const productData = [
	{
		name:"Men's Knitwear Offers",
		image:shirt1
	},
	{
		name:"Mens Sweat Shirt",
		image:shirt2
	},
	{
		name:"Mens Sweat Shirt 2",
		image:shirt3
	},

]


class Products extends Component {


	render(){
		let productCards

		if(this.props.products){
			if(this.props.products.rows){
				let productRows = this.props.products.rows

				productCards = productRows.map(product => {



					let thumbnail = "https://backendapi.turing.com/images/products/" + product.thumbnail

					return (
						<div key={product.product_id} className='col-4 text-center' style={{paddingLeft:'0px', marginBottom:'15px'}} >
							<div className='card-shadow' style={{backgroundColor:'white', height:'336px', padding:'15px'}}>
								<div style={{height:'60%', display:'flex', alignItems:'center', justifyContent:'center'}}>
									<img style={{height:'100%'}} src={thumbnail}/>
								</div>

								<div style={{height:'20%', display:'flex', alignItems:'center', justifyContent:'center'}}>
									<h6><strong>{product.name}</strong></h6>
								</div>

								<div style={{height:'10%', display:'flex', alignItems:'center', justifyContent:'center'}}>
									<button className='btn btn-sm btn-danger'>Buy Now</button>
								</div>

							</div>
						</div>
					)
				})
			}
		}


		const products = productData.map(product => {
			return (
				<div className='col-4 text-center' style={{paddingLeft:'0px', marginBottom:'15px'}} key={product.name}>
					<div className='card-shadow' style={{backgroundColor:'white', height:'336px', padding:'15px'}}>
						<div style={{height:'60%', display:'flex', alignItems:'center', justifyContent:'center'}}>
							<img style={{height:'100%'}} src={product.image}/>
						</div>

						<div style={{height:'20%', display:'flex', alignItems:'center', justifyContent:'center'}}>
							<h6><strong>{product.name}</strong></h6>
						</div>

						<div style={{height:'20%', display:'flex', alignItems:'center', justifyContent:'center'}}>
							<button className='btn btn-sm btn-danger'>Buy Now</button>
						</div>

					</div>
				</div>
			)
		});

		return(
	     	<div className='row'>
	     		{productCards}

	     	</div>
		);
	}
}


function mapStateToProps(state){
	return { 
		products:state.products.products
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({

	}, dispatch);
};


Products = connect(mapStateToProps, mapDispatchToProps)(Products);

export default withRouter(Products);
