import React, { Component } from 'react';



class FooterNewsletter extends Component {

	render(){
		return(
			<div style={{backgroundColor:'lightgray', height:'60px', paddingLeft:'16px', paddingRight:'16px', display:'flex', alignItems:'center', justifyContent:'space-between'}}>
				<div>
					<span>SUBSCRIBE FOR SHOP NEWS, UPDATES AND SPECIAL OFFERS</span>
				</div>

				<div>
					<input style={{height:'32px', display:'inline-block'}} placeholder='Your e-mail here' className='form-control'/>
				</div>

				<div>
					<button style={{display:'inline-block'}} class="btn btn-sm btn-danger">Subscribe</button>
				</div>
			</div>
		);
	}
}


export default FooterNewsletter;
