import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import Header from './components/Header'
import HeaderNavigation from './components/HeaderNavigation'
import Banner from './components/Banner'
import BannerSub from './components/BannerSub'
import Filter from './components/Filter'
import Footer from './components/Footer'
import ProductCards from './components/ProductCards'
import FooterNewsletter from './components/FooterNewsletter'


import back from './icon/icons-back-big-white.png'
import forward from './icon/icons-forward-big-white.png'

import { 
	getProducts,
} from './actions/index';



class Home extends Component {
	constructor(){
		super()

		this.state = {
			shouldRender:false,
			page:1
		}
	}


	async componentDidMount(){
		window.scrollTo(0, 0);

		await this.props.getProducts();
		this.setState({shouldRender:true});
	}


	async nextPage(){
		let nextPage = this.state.page + 1

		await this.props.getProducts(nextPage);
		this.setState({page:nextPage})
	}


	async prevPage(){
		let prevPage = this.state.page - 1

		await this.props.getProducts(prevPage);
		this.setState({page:prevPage})
	}



	render(){

		let showNext = true
		if(this.state.page*20 > this.props.count){
			showNext = false
		}

		let showPrev = false
		if(this.state.page > 1){
			showPrev = true
		}


		let pagination
		pagination = (
			<div>

				<span>
				<h3>Browse {this.props.count} Items</h3>
				</span>

				<span>
	     		<div style={{display:'inline-block', width:'60px', textAlign:'center'}}>
		     		{showPrev &&
	     				<span onClick={this.prevPage.bind(this)} style={{height:'60px', width:'60px'}}>
	     					<img style={{width:'32px', backgroundColor:'#2E2E2E'}} src={back} alt=""/> 
	     				</span> 
	     			}
	 			</div>
	 			<div style={{display:'inline-block', width:'60px', textAlign:'center'}}>
	 				<span>Page {this.state.page}</span> 
	 			</div>

	     		<div style={{display:'inline-block', width:'60px', textAlign:'center'}}>
		     		{showNext &&
	     				<span onClick={this.nextPage.bind(this)} style={{height:'60px', width:'60px'}}>
	     					<img style={{width:'32px', backgroundColor:'#2E2E2E'}} src={forward} alt=""/> 
	     				</span> 
	     			}
	 			</div>
	 			</span>

 			</div>
		)



		if(this.state.shouldRender){
			return(
			    <div>
			    	<Header/>
			    	<HeaderNavigation/>

			     	<div className='background-light-gray'>
			     		<div style={{paddingTop:'15px'}} className='container-fluid'>
				     		<Banner/>
				     	</div>

				     	<div style={{paddingTop:'15px'}} className='container'>


				     		{pagination}

					     	<ProductCards/>
				     	</div>

				     	<div style={{paddingBottom:'15px'}} className='container-fluid'>
				     		<BannerSub/>
				     	</div>
			     	</div>

			     	<FooterNewsletter />
			     	<Footer />
			    </div>
			)
		} else {
			return (<div></div>)
		}

	}
}



function mapStateToProps(state){
	return { 
		count:state.products.count,
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		getProducts:getProducts,
	}, dispatch);
};

Home = connect(mapStateToProps, mapDispatchToProps)(Home);
export default withRouter(Home);
