import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import Header from './components/Header'
import HeaderNavigation from './components/HeaderNavigation'

import { 
	updateCustomerAddress,
	updateCustomer,
} from './actions/index';



class Profile extends Component {
	constructor(){
		super()

		this.state = {
			name:'test',
			email:'test',
		}
	}



	componentWillMount(){
		console.log(this.props.user)
	}



	updateCustomer(){
		let token = this.props.user.accessToken
		this.props.updateCustomer('testdude2', 'testdude@gmail.com', token)
	}

	updateCustomerAddress(){
		let token = this.props.user.accessToken
		this.props.updateCustomerAddress(token)
	}



	render(){
		let user
		let profile

		console.log(this.props.user)

		if(this.props.user){
			user = this.props.user.customer
			profile = (
				<div>
		    		<Header/>
		    		<HeaderNavigation/>
			     	<div className='background-dark-gray'>
			     	<div style={{paddingTop:'15px', paddingBottom:'15px'}} className='container'>
			     		<div style={{padding:'15px'}}>
							<h3>Information</h3>
							<h4>Name {user.name}</h4>
							<input className='form-control' type="text" value={this.state.name} onChange={(e) => { this.setState({name: e.target.value }) }} />

							<h4>Email {user.email}</h4>
							<input className='form-control' type="text" value={this.state.email} onChange={(e) => { this.setState({email: e.target.value }) }} />

							<button onClick={this.updateCustomer.bind(this)} className='btn btn-light btn-default'>Update</button>
						</div>

						<div style={{padding:'15px'}}>
							<h3>Address</h3>
							<h4>Address 1 {user.address_1}</h4>
							<h4>Address 2 {user.address_2}</h4>
							<h4>City {user.city}</h4>
							<h4>Zipcode {user.postal_code}</h4>
							<h4>Region {user.region}</h4>
							<h4>Region ID {user.shipping_region_id}</h4>
							<button onClick={this.updateCustomerAddress.bind(this)} className='btn btn-light btn-default'>Update</button>
						</div>

					</div>
					</div>
				</div>
			)
		}

		return(
		    <div>
		    	{profile}
		    </div>
		)
	}
}



function mapStateToProps(state){
	return { 	
		user:state.user.user
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		updateCustomerAddress,
		updateCustomer,
	}, dispatch);
};

Profile = connect(mapStateToProps, mapDispatchToProps)(Profile);
export default withRouter(Profile);
