import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import Header from './components/Header'
import HeaderNavigation from './components/HeaderNavigation'

import { 
	getCart
} from './actions/index';



class Cart extends Component {
	constructor(){
		super()

		this.state = {

		}
	}


	componentDidMount(){
		window.scrollTo(0, 0)
	}


	render(){
		let cartList


		console.log(this.props.cart)
		if(this.props.cart.length > 0){

			cartList = this.props.cart.map(item => {

				let image= "https://backendapi.turing.com/images/products/" + item.image

				return (
					<tr className="d-flex">
						<td class="col-2">
							<img style={{width:'100%'}} src={image}/>
						</td>
						<td class="col-5">
							<h6><strong>{item.name}</strong></h6>
							<h6>SKU #{item.product_id}</h6>
							<h6 style={{padding:'15px'}}>Remove</h6>
						</td>
						<td class="col-2"></td>
						<td class="col-2">{item.quantity}</td>
						<td class="col-1">{item.price}</td>
					</tr>
				);
			})
		}

		return(
		    <div>
		    	<Header/>
		    	<HeaderNavigation/>


		     	<div style={{paddingTop:'15px', paddingBottom:'15px'}} className='container'>

		     	<h2>{this.props.cart.length} Items In Cart</h2>

				<table class="table table-borderless">
					<thead>
						<tr className="d-flex" style={{borderBottom:'1px solid lightgray'}}>
							<th class="col-2">Item</th>
							<th class="col-5"></th>
							<th class="col-2">Size</th>
							<th class="col-2">Quantity</th>
							<th class="col-1">Price</th>
						</tr>
					</thead>
					<tbody>
						{cartList}
					</tbody>
				</table>

				<button className='btn btn-lg btn-danger'>Checkout</button>


				</div>

		    </div>
		)
	}
}


function mapStateToProps(state){
	return { 	
		cart_id:state.cart.cart_id,
		cart:state.cart.cart,
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		getCart:getCart
	}, dispatch);
};

Cart = connect(mapStateToProps, mapDispatchToProps)(Cart);
export default withRouter(Cart);
