import {
	GET_PRODUCTS,
	SEARCH_PRODUCTS,

	GET_CATEGORIES,

	GET_DEPARTMENTS,

	GET_ATTRIBUTES,
} from './types'


import axios from 'axios';

const ROOT_URL = 'https://backendapi.turing.com'






export function getProducts(){
	//dispatch any actions with redux thunk
	return async function(dispatch){
		try {

			//page - defauls to 1
			//limit - defaults to 20
			//description_length - defaults to 200

			const products = await axios.get(ROOT_URL+'/products');

			console.log('products', products)
			return dispatch(getAllProducts(products.data));

		} catch(error){
			//If server returns error, show response
			if(error.response){
				if(error.response.data){
					console.log('ERROR')
				}
			//For other types of error, just display error
			} else if(error){
				console.log('ERROR')
			}
		}
	};
}

export function getAllProducts(products){
	return {
		type:GET_PRODUCTS,
		payload:products
	};
}




