import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import { 
	getShippingRegions,
	getShippingOptions,
} from './actions/index';



class Checkout extends Component {
	constructor(){
		super()

		this.state = {

		}
	}


	componentDidMount(){
		window.scrollTo(0, 0)

		this.props.getShippingRegions()
	}


	getOptions(region_id){
		this.props.getShippingOptions(region_id)
	}



	render(){

		let regions

		if(this.props.regions.length > 0){

			regions = this.props.regions.map(region => {
				console.log(region)
				return (
					<a onClick={ ()=>this.getOptions(region.region_shipping_id) } key={region.region_shipping_d} className="dropdown-item">{region.shipping_region}</a>
				)
			})

		}



		return(
		    <div>


				<div style={{padding:'15px'}}>
 					<h3 className='light-gray'>Region</h3>
					<div className="dropdown">
					  <button className="btn dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
					    Select Region
					  </button>
					  <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
					  	{regions}
					  </div>
					</div>
				</div> 


				<div style={{padding:'15px'}}>
 					<h3 className='light-gray'>Shipping Options</h3>
					<div className="dropdown">
					  <button className="btn dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
					    Select Shipping Option
					  </button>
					  <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
					  	{regions}
					  </div>
					</div>
				</div> 


		    </div>
		)
	}
}


function mapStateToProps(state){
	return { 	
		regions:state.orders.regions,
		options:state.orders.options,
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		getShippingRegions:getShippingRegions,
		getShippingOptions:getShippingOptions,
	}, dispatch);
};

Checkout = connect(mapStateToProps, mapDispatchToProps)(Checkout);
export default withRouter(Checkout);
