import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import Header from './components/Header'
import HeaderSub from './components/HeaderSub'
import Filter from './components/Filter'
import FilterCategory from './components/FilterCategory'
import Footer from './components/Footer'
import ProductCards from './components/ProductCards'

import queryString from 'query-string'

import { 
	getProduct,
	getProductReviews,
} from './actions/index';



class Product extends Component {
	constructor(){
		super()

		this.state = {

		}
	}



	async componentDidMount(){
		let productID = this.props.match.params.id

		this.props.getProduct(productID);
		this.props.getProductReviews(productID);
	}



	render(){


		console.log(this.props.reviews)



		let product
		if(this.props.product){
			let image= "https://backendapi.turing.com/images/products/" + this.props.product.image

			product = (
	     		<div className='row'>
	     			<div className='col-4'>
	     				<img src={image} style={{width:'100%'}}/>
	     			</div>

	     			<div className='col-8'>
	     				<h3>{this.props.product.name}</h3>
	     				<h3>{this.props.product.price}</h3>
	     			</div>
		     	</div>
			)
		}


		let reviews
		if(this.props.reviews.length>0){
			reviews = this.props.reviews.map(review => {
				return (
	     		<div className='row' style={{borderBottom:'1px solid gray'}}>
	     			<div className='col-4'>
	     				<h4>{review.rating}</h4>
	     				<h4>{review.name}</h4>
	     				<h4>{review.created_on}</h4>
	     			</div>
	     			<div className='col-8'>
	     				<p>{review.review}</p>
	     			</div>
		     	</div>
				)
			})
		}



		return(

		    <div>
		     	<Header/>
		     	<HeaderSub/>

		     	<div style={{paddingTop:'15px', paddingBottom:'15px'}} className='container-fluid background-dark-gray'>


		     		{product}

		     		{reviews}


		     	</div>

		     	<Footer />
		    </div>


		)
	}
}



function mapStateToProps(state){
	return { 
		product:state.products.product,
		reviews:state.products.reviews,
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		getProduct:getProduct,
		getProductReviews:getProductReviews,
	}, dispatch);
};

Product = connect(mapStateToProps, mapDispatchToProps)(Product);
export default withRouter(Product);
