import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';



class Footer extends Component {
	constructor(){
		super()

		this.state = {

		}
	}

	render(){

		let departmentLabels 

		if(this.props.departments){
			departmentLabels = this.props.departments.map(department => {
				return (
					<span style={{flex:'1'}} className='topbar white' key={department.key}><strong>{department.name}</strong></span>
				)
			});
		}

		return(
			<div className='background-black' style={{padding:'30px'}}>
				<div style={{display:'flex', width:'100%', justifyContent:'space-around', textAlign:'center'}}>
					{departmentLabels}
				</div>

				<div style={{display:'flex', width:'100%', justifyContent:'space-around', textAlign:'center'}}>
					<span className='topbar dark-gray' style={{flex:'1'}}>Instagram  •  Facebook  • Others</span>
				</div>


				<div style={{display:'flex', width:'100%', justifyContent:'space-around', textAlign:'center'}}>
					<span className='topbar dark-gray' style={{flex:'1'}}>©2016 shopmate Ltd  •  Contact  • Privacy policy</span>
				</div>
			</div>
		);
	}
}


function mapStateToProps(state){
	return { 
		departments:state.departments.departments
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({

	}, dispatch);
};


Footer = connect(mapStateToProps, mapDispatchToProps)(Footer);

export default withRouter(Footer);
