import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import { 
	getShippingRegions,
	getShippingOptions,
} from './actions/index';



class Checkout extends Component {
	constructor(){
		super()

		this.state = {
			selectedRegion:''
		}

	}


	componentDidMount(){
		window.scrollTo(0, 0)

		this.props.getShippingRegions()
	}



	getShippingOptions(e){
		let value = e.target.value

		let region_id
		let regions = this.props.regions
		for(let i=0; i<regions.length; i++){
			if(regions[i].shipping_region == value){
				region_id = regions[i].shipping_region_id
			}
		}

		this.props.getShippingOptions(region_id)

		this.setState({selectedRegion:value});
	}



	render(){

		let regions = []
		let options = []

		if(this.props.regions.length > 0){
			regions = this.props.regions.map(region => {
				return (
					<option key={region.region_shipping_id} className="dropdown-item">{region.shipping_region}</option>
				)
			})
		}






		return(
		    <div>


		    <div style={{padding:'15px'}}>
			    <h3 className='light-gray'>Select Shipping Region</h3>
				<select value={this.state.selectedRegion} onChange={this.getShippingOptions.bind(this)} className="form-control">
					{regions}
				</select>
			</div>


		    <div style={{padding:'15px'}}>
			    <h3 className='light-gray'>Select Shipping Option</h3>
				<select value={this.state.selectedOption} onChange={this.getShippingOptions.bind(this)} className="form-control">
					{options}
				</select>
			</div>








		    </div>
		)
	}
}


function mapStateToProps(state){
	return { 	
		regions:state.orders.regions,
		options:state.orders.options,
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		getShippingRegions:getShippingRegions,
		getShippingOptions:getShippingOptions,
	}, dispatch);
};

Checkout = connect(mapStateToProps, mapDispatchToProps)(Checkout);
export default withRouter(Checkout);
