import {
	GET_PRODUCTS
} from '../actions/types';


export default function(state={products:{}, product:{}}, action){
	switch(action.type){


	case GET_PRODUCTS:
		return {...state, products:action.payload};


	case GET_PRODUCT:
		return {...state, product:action.payload};


	default:
		return state;
	}
}
