import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import { 
	getShippingRegions,
	getShippingOptions,
} from './actions/index';



class Checkout extends Component {
	constructor(){
		super()

		this.state = {
			selectedRegion:''
		}

    	this.selectRef = React.createRef();
	}


	componentDidMount(){
		window.scrollTo(0, 0)

		this.props.getShippingRegions()
	}



	getShippingOptions(e){

		console.log(e.target.name)
		console.log(e.target.value)



		//this.setState({selectedRegion:e.target.value})


	}



	render(){

		let regions

		if(this.props.regions.length > 0){

			regions = this.props.regions.map(region => {
				console.log(region)
				return (
					<option key={region.region_shipping_id} value={region.region_shipping_id} className="dropdown-item">{region.shipping_region}</option>
				)
			})

		}



		return(
		    <div>


			<select ref={this.selectRef} onChange={this.getShippingOptions.bind(this)} className="form-control">
				{regions}
			</select>







		    </div>
		)
	}
}


function mapStateToProps(state){
	return { 	
		regions:state.orders.regions,
		options:state.orders.options,
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		getShippingRegions:getShippingRegions,
		getShippingOptions:getShippingOptions,
	}, dispatch);
};

Checkout = connect(mapStateToProps, mapDispatchToProps)(Checkout);
export default withRouter(Checkout);
