import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';


class Products extends Component {

	toProduct(parameter){
		this.props.history.push('/product/'+parameter)
	}


	render(){
		let productCards

		if(this.props.products){
			if(this.props.products.rows){
				let productRows = this.props.products.rows

				productCards = productRows.map(product => {
					console.log(product)
					let thumbnail = "https://backendapi.turing.com/images/products/" + product.image

					return (
						<div key={product.product_id} className='col-4 text-center' style={{paddingLeft:'0px', marginBottom:'15px'}} >
							<div onClick={()=>this.toProduct(product.product_id)} className='card-shadow' style={{backgroundColor:'white', height:'336px', padding:'15px'}}>
								<div style={{height:'70%', display:'flex', alignItems:'center', justifyContent:'center'}}>
									<img style={{height:'100%'}} src={thumbnail}/>
								</div>

								<div style={{height:'15%', display:'flex', alignItems:'center', justifyContent:'center'}}>
									<h3>{product.name}</h3>
								</div>

								<div style={{height:'15%', display:'flex', alignItems:'center', justifyContent:'center'}}>
									<button className='btn btn-sm btn-danger'>Buy Now</button>
								</div>

							</div>
						</div>
					)
				})
			}
		}


		return(
	     	<div className='row'>
	     		{productCards}
	     	</div>
		);
	}
}


function mapStateToProps(state){
	return { 
		products:state.products.products
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({

	}, dispatch);
};

Products = connect(mapStateToProps, mapDispatchToProps)(Products);
export default withRouter(Products);
