import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';


import shirt1 from '../images/shirt1.png'
import shirt2 from '../images/shirt2.png'
import shirt3 from '../images/shirt3.png'



const productData = [
	{
		name:"Men's Knitwear Offers",
		image:shirt1
	},
	{
		name:"Mens Sweat Shirt",
		image:shirt2
	},
	{
		name:"Mens Sweat Shirt 2",
		image:shirt3
	},

]

class Products extends Component {




	render(){


		if(this.props.products){
			if(this.props.products.rows){
				console.log(this.props.products.rows)
			}
		}


		const products = productData.map(product => {
			return (
				<div className='col-4 text-center' style={{paddingLeft:'0px', marginBottom:'16px'}} key={product.name}>

					<div className='card-shadow' style={{backgroundColor:'white', minHeight:'336px'}}>

						<div>
							<img style={{width:'100%'}} src={product.image}/>
						</div>

						<div>
							<h6><strong>{product.name}</strong></h6>
						</div>

						<div>
							<button className='btn btn-sm btn-danger'>Buy Now</button>
						</div>

					</div>

				</div>
			)
		});

		return(
	     	<div className='row'>
	     		{products}
	     		{products}
	     	</div>
		);
	}
}


function mapStateToProps(state){
	return { 
		products:state.products.products
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({

	}, dispatch);
};


Products = connect(mapStateToProps, mapDispatchToProps)(Products);

export default withRouter(Products);
