import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import Header from './components/Header'
import HeaderNavigation from './components/HeaderNavigation'
import Banner from './components/Banner'
import BannerSub from './components/BannerSub'
import Filter from './components/Filter'
import Footer from './components/Footer'
import ProductCards from './components/ProductCards'
import FooterNewsletter from './components/FooterNewsletter'


import { 
	getProducts,
} from './actions/index';



class Home extends Component {
	constructor(){
		super()

		this.state = {
			shouldRender:false,
			page:1
		}
	}


	async componentDidMount(){
		window.scrollTo(0, 0);

		await this.props.getProducts();

		this.setState({shouldRender:true});
	}

	nextPage(){
		let nextPage = this.state.page + 1

		this.props.getProducts(nextPage);

		this.setState({page:nextPage})
	}


	prevPage(){
		let prevPage = this.state.page - 1

		this.props.getProducts(prevPage);

		this.setState({page:prevPage})
	}



	render(){

		console.log('page', this.state.page)
		console.log((this.state.page+1) * 20)


		let showNext = true
		if(this.state.page*20 > this.props.count){
			showNext = false
		}

		let showPrev = false
		if(this.state.page > 1){
			showPrev = true
		}

		if(this.state.shouldRender){
			return(
			    <div>
			    	<Header/>
			    	<HeaderNavigation/>

			     	<div className='background-light-gray'>
			     		<div style={{paddingTop:'15px'}} className='container-fluid'>
				     		<Banner/>
				     	</div>

				     	<div style={{paddingTop:'15px'}} className='container'>
				     		<h3>Browse {this.props.count} Items</h3>

				     		{showPrev &&
			     				<span onClick={this.prevPage.bind(this)}>Prev</span> 
			     			}
			     			<span>Page {this.state.page}</span> 
			     			{showNext &&
			     				<span onClick={this.nextPage.bind(this)}>Next</span>
			     			}

					     	<ProductCards/>
				     	</div>

				     	<div style={{paddingBottom:'15px'}} className='container-fluid'>
				     		<BannerSub/>
				     	</div>
			     	</div>

			     	<FooterNewsletter />
			     	<Footer />
			    </div>
			)
		} else {
			return (<div></div>)
		}

	}
}



function mapStateToProps(state){
	return { 
		count:state.products.count,
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		getProducts:getProducts,
	}, dispatch);
};

Home = connect(mapStateToProps, mapDispatchToProps)(Home);
export default withRouter(Home);
