import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import { 
	register
} from './actions/index';





class Register extends Component {
	constructor(){
		super()

		this.state = {

		}
	}


	login(){
		this.props.register('joeblow', 'joeblow@gmail.com', '1234')
	}



	handleInputChange(event) {
		const value = event.target.value;
		const name = event.target.name;

		this.setState({
		  [name]: value
		});
	}


	render(){

		let registrationError
		if(this.props.registrationError){
			registrationError = this.props.registrationError.message
		}

		return(
		    <div>
				<div class="form-group">
					<h4>Name</h4>
					<input onChange={this.handleInputChange.bind(this)} type="text" class="form-control" id="exampleInputName" placeholder="Name" />
				</div>

				<div class="form-group">
					<h4>Email</h4>
					<input onChange={this.handleInputChange.bind(this)} type="email" class="form-control" id="exampleInputEmail" placeholder="Email" />
				</div>

				<div class="form-group">
					<h4>Password</h4>
					<input onChange={this.handleInputChange.bind(this)} type="password" class="form-control" id="exampleInputPassword" placeholder="Password" />
				</div>

				<button onClick={this.login.bind(this)} className="btn btn-sm btn-danger">Register</button>

				<h4 className='pink'>{registrationError}</h4>
		    </div>	
		)
	}
}



function mapStateToProps(state){
	return { 
		registrationError:state.errors.registrationError
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		register:register
	}, dispatch);
};

Register = connect(mapStateToProps, mapDispatchToProps)(Register);
export default withRouter(Register);
