import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import { 
	getShippingRegions,
	getShippingOptions,
	getTaxes,
} from './actions/index';



class Checkout extends Component {
	constructor(){
		super()

		this.state = {
			selectedRegion:'',
			selectedOption:'',
			selectedTax:'',
		}
	}


	componentDidMount(){
		window.scrollTo(0, 0)

		this.props.getShippingRegions()
		this.props.getTaxes()
	}



	selectRegionOption(e){
		let value = e.target.value
		this.props.getShippingOptions(value)
		this.setState({selectedRegion:value});
	}


	selectShippingOption(e){
		let value = e.target.value
		this.setState({selectedOption:value});
	}


	selectTaxOption(e){
		let value = e.target.value
		this.setState({selectedTax:value});
	}



	render(){

		let regions = []
		if(this.props.regions.length > 0){
			regions = this.props.regions.map(region => {
				return (
					<option key={region.shipping_region_id} value={region.shipping_region_id} className="dropdown-item">{region.shipping_region}</option>
				)
			})
		}


		let options = []
		if(this.props.options.length > 0){
			options = this.props.options.map(option => {
				return (
					<option value={option.shipping_id} key={option.shipping_id} className="dropdown-item">{option.shipping_type} - {option.shipping_cost}</option>
				)
			})
		}


		let taxes = []
		if(this.props.taxes.length > 0){
			taxes = this.props.taxes.map(tax => {
				return (
					<option value={tax.tax_id} key={tax.tax_id} className="dropdown-item">{tax.tax_percentage} - {tax.tax_type}</option>
				)
			})
		}




		return(
		    <div>

			    <div style={{padding:'15px'}}>
				    <h3 className='light-gray'>Select Tax Option</h3>
					<select value={this.state.selectedTax} onChange={this.selectTaxOption.bind(this)} className="form-control">
						{taxes}
					</select>
				</div>

			    <div style={{padding:'15px'}}>
				    <h3 className='light-gray'>Select Shipping Region</h3>
					<select value={this.state.selectedRegion} onChange={this.selectRegionOption.bind(this)} className="form-control">
						{regions}
					</select>
				</div>

			    <div style={{padding:'15px'}}>
				    <h3 className='light-gray'>Select Shipping Option</h3>
					<select value={this.state.selectedOption} onChange={this.selectShippingOption.bind(this)} className="form-control">
						{options}
					</select>
				</div>

		    </div>
		)
	}
}


function mapStateToProps(state){
	return { 	
		regions:state.orders.regions,
		options:state.orders.options,
		taxes:state.orders.taxes,
	};
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		getShippingRegions,
		getShippingOptions,
		getTaxes
	}, dispatch);
};

Checkout = connect(mapStateToProps, mapDispatchToProps)(Checkout);
export default withRouter(Checkout);
