import React, { Component } from 'react';
import { bindActionCreators } from 'redux'; 
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import Header from './components/Header'
import HeaderNavigation from './components/HeaderNavigation'
import FilterCategory from './components/FilterCategory'
import Footer from './components/Footer'
import ProductCards from './components/ProductCards'

import { 
	getCategoriesInDepartment,
	getProductsInDepartment,
	getProductsInCategory,
} from './actions/index';



class Departments extends Component {
	constructor(){
		super()

		this.state = {
			page:1
		}
	}


	componentDidMount(){
		window.scrollTo(0, 0)

		let param = this.props.match.params.id;
		this.props.getCategoriesInDepartment(param)
		this.props.getProductsInDepartment(param)
	}


	filterProducts(id){
		this.props.getProductsInCategory(id);
	}

	render(){

		let categoryLabels;

		if(this.props.categories.length > 0){
			categoryLabels = this.props.categories.map(category => {
				return (
					<div style={{paddingLeft:'15px'}} key={category.category_id}>
						<h3 className='link' onClick={() => this.filterProducts(category.category_id)} >{category.name}</h3>
					</div>
				)
			});
		}


		return(
		    <div>
		    	<Header/>
		    	<HeaderNavigation/>

		     	<div className='background-light-gray'>
			     	<div style={{paddingTop:'15px'}} className='container-fluid'>
			     	<div className='row'>

				     	<div className='col-3'>
					     	<div className='bottom-shadow' style={{backgroundColor:'white'}}>
					     		<div style={{padding:'15px'}}>
					     			<h3 className='light-gray'>Categories</h3>
					     			{categoryLabels}
					     		</div>
					     	</div>
				     	</div>

				     	<div className='col-9'>
				     		<h4>{this.props.count} Items</h4>
				     		<ProductCards/>
				     	</div>

			     	</div>
			     	</div>
		     	</div>

		     	<Footer />
		    </div>
		)
	}
}


function mapStateToProps(state){
	return { 
		count:state.products.count,
		categories:state.categories.categories,
	};	
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		getCategoriesInDepartment,
		getProductsInDepartment,
		getProductsInCategory,
	}, dispatch);
};

Departments = connect(mapStateToProps, mapDispatchToProps)(Departments);
export default withRouter(Departments);
